import React, { Component } from 'react';
import "bulma/css/bulma.css";
import './Resume.css';
import { Document, Page, pdfjs } from 'react-pdf'
import 'react-pdf/dist/Page/AnnotationLayer.css';
import throttle from "lodash.throttle"
import pdf from '../Images/Resume.pdf'
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

class Resume extends Component {
  constructor(props) {
    super(props)
    this.state = {width: null,
      space: "0%",
      spaceState: 0}
  }

  componentDidMount () {
    this.setDivSize()
    window.addEventListener("resize", throttle(this.setDivSize, 500))
  }

  componentWillUnmount () {
    window.removeEventListener("resize", throttle(this.setDivSize, 500))
  }

  setDivSize = () => {
    this.setState({width: this.pdfWrapper.getBoundingClientRect().width})
  }
  fixSpace = () => {
    if(this.props.space!=this.state.spaceState){
      if(this.props.space === 1){
        this.setState({ space: "5%", spaceState: this.props.space });
      }
      else if(this.props.space === 2){
        this.setState({ space: "10%", spaceState: this.props.space });
      }
      else if(this.props.space === 3){
        this.setState({ space: "20%", spaceState: this.props.space });
      }
      console.log(this.state.space)
    }
  }
  render() {
    return (
      <div style={{minHeight:"100vh",paddingTop:this.state.space}}>
        {console.log(this.props)}
        {this.fixSpace()}

      <div id="row" style={{ width: "100vw", display: "flex", overflow: "hidden"}}>
        <div id="placeholderWrapper" style={{width: "10vw", height: "100vh"}}/>
        <div id="pdfWrapper" style={{width: "80vw", marginTop:"1%"}} ref={(ref) => this.pdfWrapper = ref}>
          <PdfComponent wrapperDivSize={this.state.width} />
        </div>
      </div>
      </div>

    )
  }
}

class PdfComponent extends Component {
  constructor(props) {
    super(props)
    this.state = {viewPdf: false}
  }
  onResumeClick() {
    window.open(pdf);
  }
  viewResume = () => {
    this.setState({ viewPdf: !this.state.viewPdf });
  }
  displayResume = () =>{
    if(this.state.viewPdf){
    return(
      <Document
        file={pdf}
      >
        <Page pageIndex={0} width={this.props.wrapperDivSize} />
      </Document>
    )
  }
  }
  render() {
    return (
      <div>
      <div className="columns">
        <div className = "column is-half" style={{marginBottom:"1%"}}>
          <div className="button is-link" onClick={this.onResumeClick} style={{width:"100%", height:"20%"}}>Open PDF Viewer</div>
        </div>
        <div className = "column is-half" style={{marginBottom:"1%"}}>
          <div className="button is-link" onClick={this.viewResume} style={{width:"100%", height:"20%"}}>Open PDF Viewer</div>
        </div>
        </div>
        <div >
          {this.displayResume()}
        </div>
      </div>
    )
  }
}

export default Resume
