import React, { Component } from 'react';
import "bulma/css/bulma.css";
import '../MainPage.css';
// import { Route } from "react-router";
import headshot from '../Images/Headshot.jpeg'
// import family from '../Images/Family.JPG'

import Carousel, { Dots } from '@brainhubeu/react-carousel';
import Image from 'react-image-resizer';

class About extends Component {
  constructor(props) {
    super(props);
    this.state = {
      size:"200",
      padding:"50"
    };
  }

  grow=()=>{
    this.setState({size:"300", padding:"0"});
  }
  shrink=()=>{
    this.setState({size:"200", padding:"50"});
  }

  render() {
    return (
      <div  onMouseOut={() => this.shrink()} onMouseOver={() => this.grow()} style={{padding:this.state.padding}}>
      <img  mode='fit' width={this.state.size} height={this.state.size} src={this.props.image}/>

      </div>
    );
  }
}

export default About;
