import React, { Component } from 'react';
import "bulma/css/bulma.css";
import './Resume.css';
import { Document, Page, pdfjs , View } from 'react-pdf'
import resume from '../Images/Resume.pdf'

pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

const styles = ({
  page: { backgroundColor: 'tomato' },
  section: { color: 'white', textAlign: 'center', margin: 30 }
});

class Resume extends Component {
  state = {
    totalPages: null,
  }

  render() {
    const { totalPages } = this.state;

    return (
      <div id="row" style={{height: "100vh", width: "100vw", display: "flex", overflow: "hidden"}}>
        <div id="placeholderWrapper" style={{width: "10vw", height: "100vh"}}/>
        <div id="pdfWrapper" style={{width: "90vw"}} ref={(ref) => this.pdfWrapper = ref}>
      <div class="columns is-centered" style={{maxWidth:"100%"}}>
      <Document
      file={resume}

      >
        {Array.from(
          new Array(totalPages),
          (el, index) => (
                <Page
                  key={`page_${index + 1}`}
                  pageNumber={index + 1}
                />
              ),
        )}
      </Document>
      </div>
      </div>
      </div>
    );

}
}
export default Resume;
