import React, { Component } from 'react';
import "bulma/css/bulma.css";
import './MainPage.css';
import Navbar from './Components/Navbar';
import { Route, Switch, HashRouter } from 'react-router-dom'
import About from "./Pages/About";
import Resume from "./Pages/Resume";
import Contact from "./Pages/Contact";
import Background from './Images/background.png'
import history from './history';
import { Element, animateScroll as scroll } from "react-scroll";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      collapsed: 1
    };
  }

    handleWindowResize = () => {
      if(this.state.collapsed!==1 && window.innerWidth>1023){
        this.setState({ collapsed: 1 });
        console.log(this.state.collapsed)
      }
      else if(this.state.collapsed!==2 && window.innerWidth<1023 && window.innerWidth>500){
        this.setState({ collapsed: 2 });
        console.log(this.state.collapsed)
      }
      else if(this.state.collapsed!==3 && window.innerWidth<500){
        this.setState({ collapsed: 3 });
        console.log(this.state.collapsed)
      }
    }

    componentDidMount() {
      window.addEventListener('resize', this.handleWindowResize);
    }

    componentWillUnmount() {
      window.removeEventListener('resize', this.handleWindowResize);
    }

    adjustSpacing = () => {
      if(this.state.collapsed===1){
        return(
          <hr style={{border:"1px solid black",marginBottom:"5%"}}/>
        )
      }
      else if(this.state.collapsed===2){
        return(
          <hr style={{border:"1px solid black",marginBottom:"10%"}}/>
        )
      }
      else if(this.state.collapsed===3){
        return(
          <hr style={{border:"1px solid black",marginBottom:"15%"}}/>
        )
      }
    }
  render() {
    return (
      // <HashRouter basename='/'>
      <div>
        <div className="App-header">
        <div className="bg-img" >

        <div className="pinNavbar">
        <Navbar/>
        </div>
        {this.adjustSpacing()}

        </div>
        </div>
        <Element name="About" className="element" >
            <About/>
        </Element>
        <Element name="Resume" className="element" >
          <Resume/>
        </Element>
        <Element name="Project" className="element" >
            test 1
        </Element>
        <Element name="Contact" className="element" >
          <Contact/>
        </Element>
        </div>

        // </HashRouter>
    );
  }
}

export default Home;


